mapper.select:
-- select list SELECT b.*, bk.kr_nm, bk.en_nm, u.name FROM board b INNER JOIN board_kind bk on b.kind_seq = bk.kind_seq INNER JOIN users u on b.user_seq = u.user_seq WHERE b.user_seq = ${userSeq}
AND b.kind_seq in
${kindSeq}

mapper.insert:
-- insert
INSERT board (content, kind_seq, user_seq, insert_dt)
VALUES ('${content}', ${kindSeq}, ${userSeq}, now());

mapper.update:
-- update
UPDATE board
SET content = '${content}', kind_seq = ${kindSeq}
WHERE board_seq = ${boardSeq};
